#!/usr/bin/perl

use strict;
use warnings;
use Test::More;
use 5.030;

is capture("perl perl/ch-1.pl 20"), <<END;
3.
END

# build list of words for testing
ok 0==system("aspell -d en dump master | aspell -l en expand > words.txt");

is capture("perl perl/ch-2.pl world"), <<END;
or
ow
owl
old
lo
low
lord
row
rd
rod
do
wold
world
word
END


done_testing;

sub capture {
	my($cmd) = @_;
	my $out = `$cmd`;
	$out =~ s/[ \t\v\f\r]*\n/\n/g;
	return $out;
}
